// Prepare prompt for topic extraction
const items = $input.all();
const data = items[0].json;

const messagesText = data.messages_text || "";
const hours = data.hours || 24;

const prompt = `Проанализируй диалог в Telegram группе за последние ${hours} часов.\n\nСообщения:\n${messagesText}\n\nИзвлеки 3-5 основных тем обсуждения. Верни ТОЛЬКО JSON массив тем:\n{"topics": ["тема1", "тема2", "тема3"]}\n\nВАЖНО: Верни ТОЛЬКО валидный JSON без markdown блоков!`;

return [
  {
    json: {
      prompt: prompt,
      hours: hours
    }
  }
];

// Parse GigaChat response and extract topics
const items = $input.all();
const response = items[0].json.choices?.[0]?.message?.content || '{}';

let topics = [];

try {
  // Remove markdown code blocks if present
  let cleanContent = response.trim();
  if (cleanContent.startsWith('```')) {
    const lines = cleanContent.split('\n');
    cleanContent = lines.slice(1, -1).join('\n').trim();
    if (cleanContent.startsWith('json')) {
      cleanContent = cleanContent.slice(4).trim();
    }
  }

  // Parse JSON
  const parsed = JSON.parse(cleanContent);
  topics = parsed.topics || [];

} catch (e) {
  console.error('Topic extraction parse error:', e);
  console.error('Response:', response.substring(0, 200));

  // Fallback: try to extract JSON from text
  const jsonMatch = response.match(/\{[\s\S]*?\}/);
  if (jsonMatch) {
    try {
      const parsed = JSON.parse(jsonMatch[0]);
      topics = parsed.topics || [];
    } catch (e2) {
      console.error('Fallback parse failed:', e2);
      topics = [];
    }
  }
}

// Return topics array
return [
  {
    json: {
      topics: topics
    }
  }
];
