// Подготовка данных для multi-agent анализа
const items = $input.all();
const data = items[0].json;

// Извлекаем параметры
const messages = data.messages || [];
const userId = data.user_id;
const groupId = data.group_id;
const hours = data.hours || 24;

// Ограничиваем количество сообщений
const maxMessages = 200;
const limitedMessages = messages.slice(0, maxMessages);

// Форматируем сообщения для анализа
const messagesText = limitedMessages.map((msg, idx) => 
  `[${idx + 1}] ${msg.username}: ${msg.text} (${msg.date})`
).join('\n');

// Создаем промпты для каждого агента
const topicPrompt = `Проанализируй диалог в Telegram группе за последние ${hours} часов.\n\nСообщения:\n${messagesText}\n\nИзвлеки 3-5 основных тем обсуждения. Верни ТОЛЬКО JSON массив тем:\n{"topics": ["тема1", "тема2", "тема3"]}`;

const speakerPrompt = `Проанализируй кто о чем говорил в диалоге.\n\nСообщения:\n${messagesText}\n\nСоздай краткое резюме по каждому активному участнику (топ-5). Верни ТОЛЬКО JSON:\n{"speakers": {"username1": "краткое описание", "username2": "краткое описание"}}`;

const summaryPrompt = `Создай краткое резюме разговора в группе.\n\nСообщения:\n${messagesText}\n\nВерни ТОЛЬКО JSON с кратким саммари (2-3 предложения):\n{"summary": "Краткое резюме..."}`;

return [
  {
    json: {
      user_id: userId,
      group_id: groupId,
      hours: hours,
      message_count: limitedMessages.length,
      topic_prompt: topicPrompt,
      speaker_prompt: speakerPrompt,
      summary_prompt: summaryPrompt,
      messages_text: messagesText
    }
  }
];

// Объединение результатов от всех агентов
const items = $input.all();

// Извлекаем ответы от каждого агента
let topicsData = {};
let speakersData = {};
let summaryData = {};

for (const item of items) {
  const content = item.json.choices?.[0]?.message?.content || '{}';

  try {
    const parsed = JSON.parse(content);

    if (parsed.topics) {
      topicsData = parsed;
    } else if (parsed.speakers) {
      speakersData = parsed;
    } else if (parsed.summary) {
      summaryData = parsed;
    }
  } catch (e) {
    // Попытка извлечь JSON из текста
    const jsonMatch = content.match(/\{[^}]+\}/);
    if (jsonMatch) {
      try {
        const parsed = JSON.parse(jsonMatch[0]);
        if (parsed.topics) topicsData = parsed;
        if (parsed.speakers) speakersData = parsed;
        if (parsed.summary) summaryData = parsed;
      } catch (e2) {
        // Игнорируем ошибки парсинга
      }
    }
  }
}

// Формируем финальный промпт для агрегатора
const aggregatorPrompt = `На основе анализа создай структурированный дайджест разговора:\n\nТемы: ${JSON.stringify(topicsData.topics || [])}\nСпикеры: ${JSON.stringify(speakersData.speakers || {})}\nОбщее резюме: ${summaryData.summary || 'не доступно'}\n\nСоздай финальный дайджест в формате JSON:\n{\n  "topics": ["тема1", "тема2"],\n  "speakers_summary": {"user1": "описание"},\n  "overall_summary": "краткое описание всего диалога",\n  "message_count": число_сообщений\n}`;

return [
  {
    json: {
      aggregator_prompt: aggregatorPrompt,
      topics_raw: topicsData,
      speakers_raw: speakersData,
      summary_raw: summaryData,
      // Сохраняем исходные данные из первого item
      user_id: items[0].json.user_id,
      group_id: items[0].json.group_id,
      hours: items[0].json.hours,
      message_count: items[0].json.message_count
    }
  }
];

// Форматирование финального ответа
const items = $input.all();
const aggregatorResponse = items[0].json.choices?.[0]?.message?.content || '{}';

let finalResult = {
  topics: [],
  speakers_summary: {},
  overall_summary: "",
  message_count: items[0].json.message_count || 0,
  period: `${items[0].json.hours || 24} hours`
};

try {
  // Парсим ответ агрегатора
  const parsed = JSON.parse(aggregatorResponse);
  finalResult = {
    topics: parsed.topics || [],
    speakers_summary: parsed.speakers_summary || {},
    overall_summary: parsed.overall_summary || "",
    message_count: parsed.message_count || items[0].json.message_count || 0,
    period: `${items[0].json.hours || 24} hours`
  };
} catch (e) {
  // Fallback: пытаемся извлечь JSON из текста
  const jsonMatch = aggregatorResponse.match(/\{[\s\S]*\}/);
  if (jsonMatch) {
    try {
      const parsed = JSON.parse(jsonMatch[0]);
      finalResult = {
        topics: parsed.topics || [],
        speakers_summary: parsed.speakers_summary || {},
        overall_summary: parsed.overall_summary || "",
        message_count: parsed.message_count || items[0].json.message_count || 0,
        period: `${items[0].json.hours || 24} hours`
      };
    } catch (e2) {
      // Используем дефолтные значения
      console.error('Failed to parse aggregator response:', e2);
    }
  }
}

return [
  {
    json: finalResult
  }
];
